static int
_cache_load(netsnmp_cache *cache, void *vmagic)
{
    DEBUGMSGTL(("internal:dot11ConfigSysLogServerTable:_cache_load","called\n"));

    if((NULL == cache) || (NULL == cache->magic)) {
        snmp_log(LOG_ERR, "invalid cache for dot11ConfigSysLogServerTable_cache_load\n");
        return -1;
    }

    /** should only be called for an invalid or expired cache */
    netsnmp_assert((0 == cache->valid) || (1 == cache->expired));
    
    /*
     * call user code
     */
	netsnmp_container_load_item_register((netsnmp_container*)cache->magic, 
										  &dot11ConfigSysLogServerTable_register, 
										  dot11ConfigSysLogServerTable_release_rowreq_ctx);
	
    return dot11ConfigSysLogServerTable_container_load((netsnmp_container*)cache->magic);
} /* _cache_load */

