int
IllegalRowStatus_undo_setup( dot11IllegalDeviceTable_rowreq_ctx *rowreq_ctx)
{
    DEBUGMSGTL(("verbose:dot11IllegalDeviceTable:IllegalRowStatus_undo_setup","called\n"));

    /** should never get a NULL pointer */
    netsnmp_assert(NULL != rowreq_ctx);

    /*
     * TODO:455:o: |-> Setup IllegalRowStatus undo.
     */
    /*
     * copy IllegalRowStatus data
     * set rowreq_ctx->undo->IllegalRowStatus from rowreq_ctx->data.IllegalRowStatus
     */
    rowreq_ctx->undo->IllegalRowStatus = rowreq_ctx->data.IllegalRowStatus;


    return MFD_SUCCESS;
} /* IllegalRowStatus_undo_setup */

/**
 * Set the new value.
 *
 * @param rowreq_ctx
 *        Pointer to the users context. You should know how to
 *        manipulate the value from this object.
 * @param IllegalRowStatus_val
 *        A long containing the new value.
 */
int
IllegalRowStatus_set( dot11IllegalDeviceTable_rowreq_ctx *rowreq_ctx, u_long IllegalRowStatus_val )
{

    DEBUGMSGTL(("verbose:dot11IllegalDeviceTable:IllegalRowStatus_set","called\n"));

    /** should never get a NULL pointer */
    netsnmp_assert(NULL != rowreq_ctx);

    /*
     * TODO:461:M: |-> Set IllegalRowStatus value.
     * set IllegalRowStatus value in rowreq_ctx->data
     */
    int ret = MFD_SUCCESS;
    
    switch(IllegalRowStatus_val) {
    
        case RS_ACTIVE:         
        case RS_DESTROY:
            rowreq_ctx->data.IllegalRowStatus = IllegalRowStatus_val;
            ret = MFD_SUCCESS;
            break;
    
        case RS_NOTINSERVICE:
        case RS_NOTREADY:
        case RS_CREATEANDGO:
            ret = MFD_ERROR;
            break;
    
        case RS_CREATEANDWAIT:
            if(RS_NONEXISTENT == rowreq_ctx->data.IllegalRowStatus) {
                global_to_local_ID(&(rowreq_ctx->data.parameter), 
                                    rowreq_ctx->tbl_idx.blackListDeviceMacID, 
                                    WIRELESS_MAX_NUM);
                rowreq_ctx->data.IllegalRowStatus = IllegalRowStatus_val;
                ret = MFD_SUCCESS;
            }
            else {
                ret = MFD_ERROR;
            }
            break;
    
        default:
            snmp_log(LOG_WARNING, "unknow %s in IllegalRowStatus_set", IllegalRowStatus_val);
            ret = MFD_ERROR;
            break;
    }

    return ret;
    
} /* IllegalRowStatus_set */

/**
 * undo the previous set.
 *
 * @param rowreq_ctx
 *        Pointer to the users context.
 */
int
IllegalRowStatus_undo( dot11IllegalDeviceTable_rowreq_ctx *rowreq_ctx)
{

    DEBUGMSGTL(("verbose:dot11IllegalDeviceTable:IllegalRowStatus_undo","called\n"));

    netsnmp_assert(NULL != rowreq_ctx);

    /*
     * TODO:456:o: |-> Clean up IllegalRowStatus undo.
     */
    /*
     * copy IllegalRowStatus data
     * set rowreq_ctx->data.IllegalRowStatus from rowreq_ctx->undo->IllegalRowStatus
     */
    rowreq_ctx->data.IllegalRowStatus = rowreq_ctx->undo->IllegalRowStatus;

    
    return MFD_SUCCESS;
} /* IllegalRowStatus_undo */

/**
 * check dependencies
 *
 * This is useful for for tables which have dependencies between columns
 * (or rows, or tables). For example, two columns allocating a percentage
 * of something add up 100%.
 *
 * Should you need different behavior depending on which columns were
 * set, rowreq_ctx->column_set_flags will indicate which writeable columns were
 * set. The definitions for the COLUMN_*_FLAG bits can be found in
 * dot11IllegalDeviceTable_oids.h.
 * A new row will have the MFD_ROW_CREATED bit set in rowreq_flags.
 *
 * @retval MFD_SUCCESS all the changes to the row are legal
 * @retval MFD_ERROR   one or more changes are not legal
 *
 * (see README-table-dot11IllegalDeviceTable if you don't have dependencies)
 */
int
dot11IllegalDeviceTable_check_dependencies(dot11IllegalDeviceTable_rowreq_ctx *rowreq_ctx)
{
    int rc = MFD_SUCCESS;
    
    DEBUGMSGTL(("internal:dot11IllegalDeviceTable:dot11IllegalDeviceTable_check_dependencies","called\n"));

    netsnmp_assert(NULL != rowreq_ctx);

    /*
     * TODO:470:o: Check dot11IllegalDeviceTable row dependencies.
     * check that all new value are legal and consistent with each other
     */
    /*
     * check RowStatus dependencies
     */
    if (rowreq_ctx->column_set_flags & COLUMN_ILLEGALROWSTATUS_FLAG) {
        /*
         * check for valid RowStatus transition (old, new)
         * (Note: move transition check to 
         *  to catch errors earlier)
         */
        rc = check_rowstatus_transition( rowreq_ctx->undo->IllegalRowStatus,
                                         rowreq_ctx->data.IllegalRowStatus );
        if (MFD_SUCCESS != rc)
            return rc;
        
        /*
         * row creation requirements
         */
        if (rowreq_ctx->rowreq_flags & MFD_ROW_CREATED) {
            if (ROWSTATUS_DESTROY ==  rowreq_ctx->data.IllegalRowStatus) {
                rowreq_ctx->rowreq_flags |= MFD_ROW_DELETED;
            }
            else if (ROWSTATUS_CREATEANDGO ==  rowreq_ctx->data.IllegalRowStatus) {
                if ((rowreq_ctx->column_set_flags & DOT11ILLEGALDEVICETABLE_REQUIRED_COLS)
                    != DOT11ILLEGALDEVICETABLE_REQUIRED_COLS) {
                    DEBUGMSGTL(("dot11IllegalDeviceTable",
                                "required columns missing (0x%0x != 0x%0x)\n",
                                rowreq_ctx->column_set_flags, DOT11ILLEGALDEVICETABLE_REQUIRED_COLS));
                    return MFD_CANNOT_CREATE_NOW;
                }
                rowreq_ctx->data.IllegalRowStatus = ROWSTATUS_ACTIVE;
            }
        } /* row creation */
        else {
            /*
             * row change requirements
             */
            /*
             * don't allow a destroy if any other value was changed, since
             * that might call data access routines with bad info.
             *
             * you may or may not require the row be notInService before it
             * can be destroyed.
             */
            if (ROWSTATUS_DESTROY == rowreq_ctx->data.IllegalRowStatus) {
                if (rowreq_ctx->column_set_flags & ~COLUMN_ILLEGALROWSTATUS_FLAG) {
                    DEBUGMSGTL(("dot11IllegalDeviceTable",
                                "destroy must be only varbind for row\n"));
                    return MFD_NOT_VALID_NOW;
                }
                rowreq_ctx->rowreq_flags |= MFD_ROW_DELETED;

            } /* row destroy */
        } /* row change */
    }
    else {
        /*
         * must have row status to create a row
         */
        if (rowreq_ctx->rowreq_flags & MFD_ROW_CREATED) {
            DEBUGMSGTL(("dot11IllegalDeviceTable",
                        "must use RowStatus to create rows\n"));
            return MFD_CANNOT_CREATE_NOW;
        }
    } /* row status not set */

    if ( MFD_SUCCESS != rc )
        return rc;

    return rc;
} /* dot11IllegalDeviceTable_check_dependencies */

