/**
 * Set the new value.
 *
 * @param rowreq_ctx
 *        Pointer to the users context. You should know how to
 *        manipulate the value from this object.
 * @param permitBSSIDRowStatus_val
 *        A long containing the new value.
 */
int
permitBSSIDRowStatus_set( dot11PermitBSSIDTable_rowreq_ctx *rowreq_ctx, u_long permitBSSIDRowStatus_val )
{

    DEBUGMSGTL(("verbose:dot11PermitBSSIDTable:permitBSSIDRowStatus_set","called\n"));

    /** should never get a NULL pointer */
    netsnmp_assert(NULL != rowreq_ctx);

    /*
     * TODO:461:M: |-> Set permitBSSIDRowStatus value.
     * set permitBSSIDRowStatus value in rowreq_ctx->data
     */
    
    int ret = MFD_SUCCESS;
    
    switch(permitBSSIDRowStatus_val) {
    
		case RS_NOTINSERVICE:
        case RS_ACTIVE:         
        case RS_DESTROY:
            rowreq_ctx->data.permitBSSIDRowStatus = permitBSSIDRowStatus_val;
            ret = MFD_SUCCESS;
            break;
    
        case RS_NOTREADY:
        case RS_CREATEANDGO:
            ret = MFD_ERROR;
            break;
    
        case RS_CREATEANDWAIT:
            if(RS_NONEXISTENT == rowreq_ctx->data.permitBSSIDRowStatus) {
                global_to_local_ID(&(rowreq_ctx->data.parameter), 
                                    rowreq_ctx->tbl_idx.permitBSSIDID, 
                                    WIRELESS_MAX_NUM);
                rowreq_ctx->data.permitBSSIDRowStatus = permitBSSIDRowStatus_val;
                ret = MFD_SUCCESS;
            }
            else {
                ret = MFD_ERROR;
            }
            break;
    
        default:
            snmp_log(LOG_WARNING, "unknow %s in permitBSSIDRowStatus_set", permitBSSIDRowStatus_val);
            ret = MFD_ERROR;
            break;
    }

    return ret;
    
} /* permitBSSIDRowStatus_set */

