            DEBUGMSGTL(("dot11SSIDConfigTable:mfd","error %d from "
                        "dot11SSIDConfigTable_set_column\n", rc));
            netsnmp_set_request_error(agtreq_info, requests, SNMP_VALIDATE_ERR(rc));
