/**
 * @internal
 */
static int
_cache_load(netsnmp_cache *cache, void *vmagic)
{
    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_cache_load","called\n"));

    if((NULL == cache) || (NULL == cache->magic)) {
        snmp_log(LOG_ERR, "invalid cache for dot11SSIDConfigTable_cache_load\n");
        return -1;
    }

    /** should only be called for an invalid or expired cache */
    netsnmp_assert((0 == cache->valid) || (1 == cache->expired));
    
    /*
     * call user code
     */
	netsnmp_container_load_item_register((netsnmp_container*)cache->magic, 
										  &dot11SSIDConfigTable_register, 
										  dot11SSIDConfigTable_release_rowreq_ctx);
	
    return dot11SSIDConfigTable_container_load((netsnmp_container*)cache->magic);
} /* _cache_load */

/**
 * @internal
 */
static void
_cache_free(netsnmp_cache *cache, void *magic)
{
    netsnmp_container *container;

    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_cache_free","called\n"));

    if((NULL == cache) || (NULL == cache->magic)) {
        snmp_log(LOG_ERR, "invalid cache in dot11SSIDConfigTable_cache_free\n");
        return;
    }

    container = (netsnmp_container*)cache->magic;

    _container_free(container);
} /* _cache_free */

/**
 * @internal
 */
static void
_container_item_free(dot11SSIDConfigTable_rowreq_ctx *rowreq_ctx, void *context)
{
    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_container_item_free","called\n"));

    if(NULL == rowreq_ctx)
        return;

    if(RS_ACTIVE == rowreq_ctx->data.SSIDRowStatus)
		dot11SSIDConfigTable_release_rowreq_ctx(rowreq_ctx);
    else 
        netsnmp_register_item(&dot11SSIDConfigTable_register, rowreq_ctx);
} /* _container_item_free */

/**
 * @internal
 */
static void
_container_free(netsnmp_container *container)
{
    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_container_free","called\n"));

    if (NULL == container) {
        snmp_log(LOG_ERR, "invalid container in dot11SSIDConfigTable_container_free\n");
        return;
    }

    /*
     * call user code
     */
    dot11SSIDConfigTable_container_free(container);
    
    /*
     * free all items. inefficient, but easy.
     */
    CONTAINER_CLEAR(container,
                    (netsnmp_container_obj_func *)_container_item_free,
                    NULL);
} /* _container_free */

/**
 * @internal
 * initialize the container with functions or wrappers
 */
void
_dot11SSIDConfigTable_container_init(dot11SSIDConfigTable_interface_ctx *if_ctx)
{
    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_dot11SSIDConfigTable_container_init","called\n"));

    /*
     * cache init
     */
    if_ctx->cache = netsnmp_cache_create(30, /* timeout in seconds */
                                         _cache_load, _cache_free,
                                         dot11SSIDConfigTable_oid,
                                         dot11SSIDConfigTable_oid_size);

    if(NULL == if_ctx->cache) {
        snmp_log(LOG_ERR, "error creating cache for dot11SSIDConfigTable\n");
        return;
    }

    if_ctx->cache->flags = NETSNMP_CACHE_DONT_INVALIDATE_ON_SET;

    dot11SSIDConfigTable_container_init(&if_ctx->container, if_ctx->cache);
    if(NULL == if_ctx->container)
        if_ctx->container = netsnmp_container_find("dot11SSIDConfigTable:table_container");
    if(NULL == if_ctx->container) {
        snmp_log(LOG_ERR,"error creating container in "
                 "dot11SSIDConfigTable_container_init\n");
        return;
    }

    if (NULL != if_ctx->cache)
        if_ctx->cache->magic = (void*)if_ctx->container;
} /* _dot11SSIDConfigTable_container_init */

/**
 * @internal
 * shutdown the container with functions or wrappers
 */
void
_dot11SSIDConfigTable_container_shutdown(dot11SSIDConfigTable_interface_ctx *if_ctx)
{
    DEBUGMSGTL(("internal:dot11SSIDConfigTable:_dot11SSIDConfigTable_container_shutdown","called\n"));

    dot11SSIDConfigTable_container_shutdown(if_ctx->container);

    _container_free(if_ctx->container);

} /* _dot11SSIDConfigTable_container_shutdown */

