dot11SSIDConfigTable_rowreq_ctx *
dot11SSIDConfigTable_row_find_by_mib_index(dot11SSIDConfigTable_mib_index *mib_idx)
{
    dot11SSIDConfigTable_rowreq_ctx   *rowreq_ctx;
    oid                      oid_tmp[MAX_OID_LEN];
    netsnmp_index            oid_idx;
    int                      rc;

    /*
     * set up storage for OID
     */
    oid_idx.oids = oid_tmp;
    oid_idx.len = sizeof(oid_tmp)/sizeof(oid);

    /*
     * convert
     */
    rc = dot11SSIDConfigTable_index_to_oid(&oid_idx, mib_idx);
    if (MFD_SUCCESS != rc)
        return NULL;

    rowreq_ctx = CONTAINER_FIND(dot11SSIDConfigTable_if_ctx.container, &oid_idx);

    return rowreq_ctx;
}

